mapper.select:
    select *
    from(
		select rownum rnum, motel_num, motel_user_num, motel_people, motel_room, motel_bathroom, motel_title, c, user_fnm, user_lnm, motel_price
		from(
			select m.motel_num, m.motel_user_num, m.motel_people, m.motel_room, m.motel_bathroom, m.motel_title, m.c, user_tb.user_fnm, user_tb.user_lnm, m.motel_price
			from(
				select motel.MOTEL_NUM, motel.USER_NUM as motel_user_num, motel.MOTEL_PEOPLE, motel.MOTEL_ROOM, motel.MOTEL_BATHROOM, motel.MOTEL_TITLE, count(*) as c , motel.motel_price
				from motel_tb motel, motel_date_tb date_tb
				where date_tb.motel_ready = 'Y'
				and motel.motel_num = date_tb.motel_num
				and motel.motel_st = 0

				and date_tb.motel_date between to_Date(#{startDate},'RR-MM-dd') and to_Date(#{endDate},'RR-MM-dd')



				and (motel.motel_city_ko = #{city} OR motel.motel_city_en = #{city})	



				and motel.motel_people >= #{motel_people}



				and motel.MOTEL_TYPE= #{motel_type}



				and motel.motel_category = #{motel_category}



				and motel_price between #{motel_price1} and #{motel_price2}


				having count(*) = to_Date(#{endDate},'RR-MM-dd') - to_Date(#{startDate},'RR-MM-dd') + 1
				group by motel.MOTEL_NUM, motel.USER_NUM, motel.MOTEL_PEOPLE, motel.MOTEL_ROOM, motel.MOTEL_BATHROOM, motel.MOTEL_TITLE, motel.motel_price
				order by motel.motel_num
			)m ,user_tb
			where m.motel_user_num = user_tb.user_num
			order by m.motel_num desc
		)
        order by rnum
        )
		where rnum between #{firstRow} and #{endRow}

select motel.MOTEL_NUM, motel.MOTEL_TYPE, motel.MOTEL_CATEGORY, motel.MOTEL_PEOPLE, motel.MOTEL_ROOM, motel.MOTEL_BATHROOM, motel.MOTEL_NATION_EN, motel.MOTEL_NATION_KO, motel.MOTEL_CITY_EN, motel.MOTEL_CITY_KO, motel.MOTEL_ADDRESS, motel.MOTEL_INTRO, motel.MOTEL_TITLE, motel.MOTEL_PRICE, motel.MOTEL_AVG, user1.USER_FNM, user1.USER_LNM, user1.user_num
from motel_tb motel, user_tb user1
where motel.motel_num = #{num} and user1.user_num=#{host}

{
	call trip_date(
		#{start_number, mode=IN},
		#{end_number, mode=IN},
		#{insert_motel_number, mode=IN},
		#{start_date, mode=IN}
	)
}

select *
from(
	select rownum as rnum, MOTEL_NUM, USER_NUM, CONTENT, user_id, motel_reply_date, user_fnm, user_lnm, motel_reply_sq
	from(
		select reply.motel_num, reply.user_num, reply.content, user_tb.user_id, reply.motel_reply_date, user_tb.user_fnm, user_tb.user_lnm, reply.motel_reply_sq
		from motel_reply reply, user_tb user_tb
		where user_tb.user_num = reply.user_num
		and reply.motel_reply_st = 0
		order by MOTEL_REPLY_SQ desc
	)
	where motel_num = #{num}
	)
where rnum between #{firstRow} and #{endRow}

mapper.update:
update motel_avg_tb
	set avg = #{star-input}
	where user_key = #{user_num}

update motel_reply
set motel_reply_st = 1
where user_num = #{user_num}
	  and motel_reply_sq = #{reply}

mapper.select:
		select * from DECLARATION_TB
		where motel_reply_num = #{reply_num}
      		  and d_user_num = #{duser_num}
      		  and user_num = #{user_num}

		select * from DECLARATION_TB
		where motel_num = #{num}
      		  and d_user_num = #{host}
      		  and user_num = #{user_num}

mapper.update:
update motel_tb
set motel_st = 1
where user_num = #{user_num}
and motel_num = #{motel_num}

update user_tb
set USER_POINT = user_point+#{price}
where user_num = #{host}

