mapper.update:
UPDATE USER_TB
   SET USER_FAILURE_COUNT = USER_FAILURE_COUNT + 1
 WHERE USER_ID = #{user_id}

UPDATE USER_TB
   SET USER_LAST_TRY_LOGIN_TIME = sysdate
 WHERE USER_ID = #{user_id}

mapper.select:
SELECT 	USER_FAILURE_COUNT
FROM 	USER_TB
WHERE 	USER_ID = #{user_id}

mapper.update:
UPDATE USER_TB
   SET enabled = 0
 WHERE USER_ID = #{user_id}

UPDATE USER_TB
   SET USER_FAILURE_COUNT = 0
 WHERE USER_ID = #{user_id}

UPDATE USER_TB
   SET USER_LAST_TRY_LOGIN_TIME = null
 WHERE USER_ID = #{user_id}

UPDATE USER_TB
   SET USER_LAST_LOGIN_TIME = sysdate
 WHERE USER_ID = #{user_id}
